styles:
@page {
  size: A4;
  margin: 20mm 15mm;
}

body {
  font-family: Arial, sans-serif;
  font-size: 10pt;
  line-height: 1.4;
  color: #333;
}

.header {
  text-align: center;
  margin-bottom: 30px;
  border-bottom: 2px solid #2563eb;
  padding-bottom: 15px;
}

.header h1 {
  font-size: 18pt;
  margin: 0 0 5px 0;
  color: #1e40af;
}

.header h2 {
  font-size: 14pt;
  margin: 0 0 10px 0;
  color: #64748b;
  font-weight: normal;
}

.report-info {
  display: flex;
  justify-content: space-between;
  font-size: 9pt;
  color: #64748b;
}

.filters {
  background: #f8fafc;
  padding: 10px;
  border-radius: 5px;
  margin-bottom: 20px;
  border-left: 4px solid #2563eb;
}

.filters h3 {
  margin: 0 0 10px 0;
  font-size: 11pt;
  color: #1e40af;
}

.filters p {
  margin: 2px 0;
  font-size: 9pt;
}

.data-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
}

.data-table th {
  background: #2563eb;
  color: white;
  padding: 8px 6px;
  text-align: left;
  font-size: 9pt;
  font-weight: bold;
}

.data-table td {
  padding: 6px;
  border-bottom: 1px solid #e2e8f0;
  font-size: 8pt;
}

.data-table tbody tr:nth-child(even) {
  background: #f8fafc;
}

.data-table .totals {
  background: #e2e8f0 !important;
  font-weight: bold;
}

.data-table .totals td {
  border-top: 2px solid #2563eb;
  padding: 8px 6px;
}

.footer {
  margin-top: 30px;
  padding-top: 15px;
  border-top: 1px solid #e2e8f0;
  text-align: center;
  font-size: 9pt;
  color: #64748b;
}

.footer p {
  margin: 2px 0;
}
